import React, { useState, useMemo, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Zap, 
  Settings2, 
  Type, 
  ArrowRight, 
  Info,
  RefreshCcw
} from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Cell } from 'recharts';

// Utility for Softmax calculation
const softmax = (arr) => {
  const max = Math.max(...arr);
  const exps = arr.map(x => Math.exp(x - max));
  const sum = exps.reduce((a, b) => a + b, 0);
  return exps.map(x => x / sum);
};

// Dot product of two vectors
const dotProduct = (a, b) => a.reduce((sum, val, i) => sum + val * (b[i] || 0), 0);

// Generate random vectors for words
const generateVectors = (words, dim) => {
  const vectors = {};
  words.forEach(word => {
    vectors[word] = {
      q: Array.from({ length: dim }, () => (Math.random() * 2 - 1).toFixed(2)),
      k: Array.from({ length: dim }, () => (Math.random() * 2 - 1).toFixed(2)),
      v: Array.from({ length: dim }, () => (Math.random() * 2 - 1).toFixed(2)),
    };
  });
  return vectors;
};

export default function ScaledAttentionPlayground() {
  const [inputText, setInputText] = useState("The cat sat");
  const [dk, setDk] = useState(64);
  const [useScaling, setUseScaling] = useState(true);
  const [selectedWordIdx, setSelectedWordIdx] = useState(0);
  const [vectors, setVectors] = useState({});

  const words = useMemo(() => inputText.split(/\s+/).filter(w => w.length > 0), [inputText]);
  const vectorDim = 4;

  // Initialize or update vectors when words change
  useEffect(() => {
    setVectors(prev => {
      const newVectors = { ...prev };
      let updated = false;
      words.forEach(word => {
        if (!newVectors[word]) {
          newVectors[word] = {
            q: Array.from({ length: vectorDim }, () => (Math.random() * 2 - 1).toFixed(2)),
            k: Array.from({ length: vectorDim }, () => (Math.random() * 2 - 1).toFixed(2)),
            v: Array.from({ length: vectorDim }, () => (Math.random() * 2 - 1).toFixed(2)),
          };
          updated = true;
        }
      });
      return updated ? newVectors : prev;
    });

    if (selectedWordIdx >= words.length) setSelectedWordIdx(0);
  }, [words]);

  // Calculation Logic with safety checks to prevent 'undefined' errors
  const attentionStats = useMemo(() => {
    if (!words.length || words.length === 0) return null;

    const queryWord = words[selectedWordIdx];
    // Safety check: ensure the query word exists in the vectors dictionary
    if (!vectors[queryWord]) return null;

    const q = vectors[queryWord].q.map(Number);

    // 1. Calculate Dot Products (Q * K^T) - using safety checks for each key word
    const dotProducts = words.map(w => {
      const keyVec = vectors[w]?.k;
      return keyVec ? dotProduct(q, keyVec.map(Number)) : 0;
    });

    // 2. Apply Scaling
    const scaleFactor = useScaling ? Math.sqrt(dk) : 1;
    const scaledScores = dotProducts.map(s => s / scaleFactor);

    // 3. Softmax
    const weights = softmax(scaledScores);

    // 4. Output (Weighted sum of V)
    const outputVector = Array(vectorDim).fill(0);
    weights.forEach((weight, i) => {
      const v = vectors[words[i]]?.v;
      if (v) {
        v.forEach((val, j) => {
          outputVector[j] += weight * Number(val);
        });
      }
    });

    return {
      queryWord,
      dotProducts,
      scaledScores,
      weights,
      outputVector,
      scaleFactor
    };
  }, [words, vectors, selectedWordIdx, dk, useScaling]);

  const handleVectorChange = (word, type, index, value) => {
    setVectors(prev => ({
      ...prev,
      [word]: {
        ...prev[word],
        [type]: prev[word][type].map((v, i) => i === index ? value : v)
      }
    }));
  };

  const chartData = (attentionStats && words.length > 0) ? words.map((w, i) => ({
    name: w,
    weight: ((attentionStats.weights[i] || 0) * 100).toFixed(1),
    score: (attentionStats.scaledScores[i] || 0).toFixed(2)
  })) : [];

