                  <div className="grid grid-cols-4 gap-1">
                    {['q', 'k', 'v'].map(type => (
                      <div key={type} className="flex flex-col gap-1">
                        <span className="text-[9px] uppercase text-slate-500 font-bold text-center">{type}</span>
                        {vectors[word]?.[type]?.map((val, i) => (
                          <input 
