                    />
                    <Bar dataKey="weight" radius={[4, 4, 0, 0]} name="Weight (%)">
                      {chartData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={index === selectedWordIdx ? '#3b82f6' : '#10b981'} fillOpacity={0.8} />
