                  <div className="flex flex-wrap gap-1 mt-2">
                    {attentionStats?.weights ? attentionStats.weights.map((w, i) => (
                      <div key={i} className="h-6 flex items-center bg-emerald-500/20 px-2 rounded border border-emerald-500/30 text-[10px] font-mono">
                        {words[i]}: {(w * 100).toFixed(0)}%
